mapper.update:
UPDATE member
SET nickname = #{nickname},
    email = #{email}
WHERE id = #{id}

UPDATE member
SET point = #{point}
WHERE id = #{id}

UPDATE member
SET point = #{point},
    version = #{originalVersion}+1
WHERE id = #{id}
AND version = #{originalVersion}

UPDATE member
SET profile_image = #{profileImage}
WHERE id = #{id}
